import React, { Component } from 'react';

class Btn extends Component {    
    constructor(){
        super();
        this.state = {
            color : "#B2DFFC",
        }
    }
    render() {
        console.log(this.props)
        if(this.props.idLength > 0 && this.props.pwLength){
            this.setState({
                color: "#0095F6"
            })
        }
        return (
            <div>
                <button 
                style={{backgroundColor: this.state.color}} 
                type="button" 
                className="loginButton">로그인</button>
            </div>
        )
    }
}

export default Btn;
