import React, { Component } from 'react';

class Btn extends Component {    
    constructor(props){
        super(props);
        this.state = {
            color : "#B2DFFC",
        }
    }
    render() {
        if(this.props.id > 0){
            this.setState({
                color : "black"
            })
        }
        return (
            <div>
                <button 
                style={{backgroundColor: this.state.color}} 
                type="button" 
                className="loginButton">로그인</button>
            </div>
        )
    }
}

export default Btn;
