import React, { Component } from 'react';
import Btn from "../../btn/btn.js";
import './login.css';

class Login extends Component {
    constructor(){
        super();
        this.state = {
            idLength : '',
            pwLength : '',
            backColor : "#0095F6",
            state : false
        }

    }

    handleIdChange = (e) => {
        this.setState({
            idLength : e.target.value.length
        })
    };

    handlePwChange = (e) => {
        this.setState({
            pwLength : e.target.value.length
        })
    };



    render() {
        return (

            <container>
                <header>
                    <img src="/images/logo_text.png" />
                </header>
                <content>
                    <div className="loginInput">
                        <input onChange={this.handleIdChange} type="text" placeholder="전화번호, 사용자 이름 또는 이메일" />
                        <input onChange={this.handlePwChange} type="password" placeholder="비밀번호" />
                    </div>

                    <div>
                    <Btn
                    id={this.state.idLength} 
                    pw={this.state.pwLength}
                    backColor={
                        this.state.idLength > 0 && this.state.pwLength > 0 ? "#B2DFFC" : "#0095F6"}
                    />
                        {/* <button type="button" id="loginButton">로그인</button> */}
                    </div>
                </content>
                <footer>
                    비밀번호를 잊으셨나요?
                </footer>
            </container>
        )
    }
}

export default Login;
